export function getImgList(){const r={urls:[],title:""};var imgdom;for(let dom of document.getElementsByClassName("description")[0].children)"A"==dom.tagName&&dom.getElementsByTagName("IMG")[0]&&r.urls.push(getUrlFromSrcset(dom.getElementsByTagName("IMG")[0].srcset));var locate=location.href.split(/\/|\./g);return r.title=locate[locate.length-1],"html"!=r.title&&""!=r.title||(r.title=locate[locate.length-2]),r.title="ero-manga-kingdom-"+r.title,r}function getUrlFromSrcset(srcset){var max={url:"",size:0};for(let temp of srcset.split(", ")){let[url,size]=temp.split(" ");size=size.split("w")[0],max.size<size&&(max.url=url,max.size=+size)}return max.url}